mapper.update:
UPDATE doctor
SET doctorPwd = #{doctorPwd}, doctorNumber = #{doctorNumber}, doctorName = #{doctorName}, doctorAge = #{doctorAge},
    doctorEmail = #{doctorEmail}, doctorImg = #{doctorImg}, career = #{career}, ancestry = #{ancestry},
    society = #{society}, subjectName = #{subjectName}
WHERE doctorId = #{doctorId}
